import matplotlib.pyplot as plt
import matplotlib.ticker as ticker
import numpy as np

def main():

	hash1 = ""
	hash2 = ""
	hash3 = ""
	hash4 = ""
	hash5 = ""
	#hash6 = ""
	merge = hash1 + hash2 + hash3 + hash4 + hash5

	plt.title("Probability Test", ha="center", fontsize=19, fontweight="bold")
	plt.xlabel("Hex value", fontsize=10, ha="center")
	plt.ylabel("Probability (%)", fontsize=10, ha="center")

	p = [0] * 16
	ave = [6.25] * 16
	for i in merge:
		p[int(i, 16)] += 1
	
	for i in range(len(p)):
		p[i] = (p[i] / 2560) * 100

	plt.plot(np.arange(16), p, 0.5)
	plt.plot(np.arange(16), ave, color="r")
	plt.xticks(np.arange(0, 16, 1))
	plt.ylim(0, 10, 1)
	plt.show()

main()